    void fooBar(int n) {
        for( int i = 1; i <= n; i++ ) {
            string output = "";
            if( i % 3 == 0 ) output += "Star";
            if( i % 5 == 0 ) output += "Coder";
            if( output != "" ) cout << output << endl;
            else cout << i << endl;
        }
