        for (int j = 0; j < numNutrientsInItem; j++) {
            int nutrient; cin >> nutrient;
            nutrientMask[i] = bitToggle(nutrientMask[i], nutrient - 1);
        }
    }
    memset(dp, -1, sizeof dp);
    cout << max(0LL, maximizeDeliciousness(0, 0)) << "\n";
}

